javascript:(function(){document.querySelectorAll(".css-panel, #css-or-die-style").forEach(el=>el.remove());const fragment=document.createDocumentFragment();const style=document.createElement("style");style.id="css-or-die-style";style.textContent=`\n    .css-panel {\n      position: fixed;\n      top: 10px;\n      left: 10px;\n      background: #fff;\n      color: #000;\n      padding: 15px;\n      border-radius: 5px;\n      box-shadow: 0 0 10px rgba(0, 0, 0, .2);\n      z-index: 99999;\n      max-width: 400px;\n      max-height: 70vh;\n      overflow: auto;\n      font-family: -apple-system, system-ui, sans-serif;\n      font-size: 14px;\n    }\n    #css-panel-title {\n      font-size: 1.2em;\n      font-weight: bold;\n      margin: 0 0 0.5em 0;\n    }\n    .css-panel h2 {\n      font-size: 1em;\n      margin-top: 1em;\n      margin-bottom: 0.3em;\n    }\n    .css-panel code {\n      background: #f5f5f5;\n      padding: 0.1rem 0.3rem;\n      border-radius: 3px;\n    }\n    .css-panel details {\n      margin-bottom: 0.5em;\n    }\n    .css-close {\n      float: right;\n      cursor: pointer;\n      font-size: 1.2em;\n      border: none;\n      background: transparent;\n    }\n    .css-empty {\n      color: green;\n      font-weight: bold;\n    }\n  `;fragment.appendChild(style);const forbiddenTags=["basefont","big","blink","center","font","marquee","s","strike","tt"];if(document.doctype?.name?.toLowerCase()!=="html"){forbiddenTags.push("u")}const forbiddenAttrs=["align","alink","background","bgcolor","border","cellpadding","cellspacing","char","charoff","clear","compact","color","frameborder","hspace","link","marginheight","marginwidth","text","valign","vlink","vspace"];const tagResults=forbiddenTags.map(tag=>({tag:tag,elements:Array.from(document.getElementsByTagName(tag))})).filter(r=>r.elements.length>0);const attrResults=[];document.querySelectorAll("*").forEach(el=>{forbiddenAttrs.forEach(attr=>{if(el.hasAttribute(attr)){attrResults.push({el:el,attr:attr})}});if(el.hasAttribute("width")||el.hasAttribute("height")){const allowedTags=["img","object","embed","video","canvas","svg","iframe"];if(!allowedTags.includes(el.tagName.toLowerCase())){if(el.hasAttribute("width"))attrResults.push({el:el,attr:"width"});if(el.hasAttribute("height"))attrResults.push({el:el,attr:"height"})}}});const tagList=tagResults.length?tagResults.map(r=>`\n        <details open>\n          <summary><code>&lt;${r.tag}&gt;</code> (${r.elements.length})</summary>\n          <ul>\n            ${r.elements.map(el=>`<li><code>${el.outerHTML.replace(/</g,"&lt;").slice(0,80)}...</code></li>`).join("")}\n          </ul>\n        </details>\n      `).join(""):'<p class="css-empty">✅ No forbidden tags found</p>';const attrList=attrResults.length?`<ul>${attrResults.map(r=>`<li><code>${r.attr}</code> on <code>&lt;${r.el.tagName.toLowerCase()}&gt;</code></li>`).join("")}</ul>`:'<p class="css-empty">✅ No forbidden attributes found</p>';const panel=document.createElement("div");panel.className="css-panel";panel.setAttribute("role","dialog");panel.setAttribute("aria-modal","true");panel.setAttribute("aria-labelledby","css-panel-title");panel.innerHTML=`\n    <button class="css-close" aria-label="Close">&times;</button>\n    <p id="css-panel-title">CSS or Die</p>\n    <h2>Forbidden Tags (${tagResults.reduce((sum,r)=>sum+r.elements.length,0)})</h2>\n    ${tagList}\n    <h2>Forbidden Attributes (${attrResults.length})</h2>\n    ${attrList}\n  `;fragment.appendChild(panel);panel.querySelector(".css-close").addEventListener("click",()=>{panel.remove();style.remove()});requestAnimationFrame(()=>{document.body.insertBefore(fragment,document.body.firstChild)})})();